// **************ADC0_Init*********************
// ADC initialization for the slider pot
// Input: none
// Output: none
// Note: Max sample rate: <=125,000 samples/second
// 			SS3 triggering event: software trigger
// 			SS3 1st sample source:  channel 1
// 			SS3 interrupts: enabled but not promoted to controller
void ADC0_Init(void){ 
	volatile unsigned long delay;
	SYSCTL_RCGC2_R |= 0x00000010;			// activate clock on port E
	delay = SYSCTL_RCGC2_R;	
	GPIO_PORTE_DIR_R &= ~0x04;				// make PE2 input (channel 1)
	GPIO_PORTE_AMSEL_R |= 0x04; 			// enable analog function on PE2
	GPIO_PORTE_AFSEL_R |= 0x04;				// enable alternate function on PE2
	GPIO_PORTE_DEN_R &= ~0x04;				// disable digital I/O on PE2
	
	// initiate device driver. Initialize ADC0, channel 1, sequencer 3
	SYSCTL_RCGC0_R |= 0x00010000;			// activiate ADC0
	delay = SYSCTL_RCGC0_R;
	SYSCTL_RCGC0_R &= ~0x00000300;		// configure for 125k
	ADC0_SSPRI_R = 0x0123;						// make sequencer 3 the highest proprity
	ADC0_ACTSS_R &= ~0x0008;					// disable seq 3
	ADC0_EMUX_R &= ~0xF000;						// set bit 15-12 for software trigger (seq3)
	ADC0_SSMUX3_R &= ~0x000F;					// clear SS3 field
	ADC0_SSMUX3_R += 1;								// set channel 1 (Ain1 = PE2)
	ADC0_SSCTL3_R = 0x0006;						// TS0 = 0 measure analog voltage on PE2 (ADC analog input pin)
																		// IE0 = 1 to set INR3 flag in ADC0_RIS_R when ADC conversion completes
																		// END0 = 1 this sample is the end of the sequence
																		// D0 = 0 sample single-ended analog input
	ADC0_ACTSS_R |= 0x0008;						// enable seq 3  
}

//*********************ADC0_In************************
// Busy-wait Analog to digital conversion
// Input: none
// Output: 12-bit result of ADC conversion
// Note: read from FIFO when a flag is set inidcating
// 			data is ready then acknowledge completion of
//			reading
unsigned long ADC0_In(void){  
	unsigned long result;
	ADC0_PSSI_R = 0x0008;							// initiate SS3
	while ((ADC0_RIS_R&0x08)==0){};		// wait until flag is set
	result = ADC0_SSFIFO3_R&0xFFF;		// read result from FIFO of sequencer 3
	ADC0_ISC_R = 0x0008;							// clear RIS by clearing bit 3 in ISC	to acknowledge completion
  return result; 										// return result
}
// **************Player_Move*********************
// subroutine to control player's moves
// Input: ADC value
// Output: 
// Note: ...
unsigned char Player_Move(void){ 
	unsigned char distance;
	unsigned long adc = 0;
	int i;

  for (i=0;i<5;i++){					// average 5 ADC readings
		adc += ADCdata;
	}
	adc /= 5;
	if (adc < 80){
		distance = 0;
	}
	else if((adc >=80)&&(adc < 130)){
		distance = 16;
	}
	else if((adc >=130)&&(adc < 160)){
		distance = 32;
	}
	else if((adc >=160)&&(adc < 270)){
		distance = 48;
	}
	else{
		distance = 64;
	}
	return distance;
}
// **************SysTick_Init*********************
// System clock initialization to run at 30 Hz 
// Input: None
// Output: None
// Note: clock initialization with priority of 0
//				for buttons, LEDs, and slider pot
void SysTick_Init(void){ 
	// pins configuration
  PORTS_Init();												// initialize switched and LEDs ports
	
	// SysTick interrupt configuration
	NVIC_ST_CTRL_R = 0; 								// disable SysTick during initialization
	NVIC_ST_RELOAD_R = 2666665; 				// reload value for 30 Hz (1/60 interrupt)
	NVIC_ST_CURRENT_R = 0; 							// write to current to clear it
	NVIC_SYS_PRI3_R = (NVIC_SYS_PRI3_R& 0x00FFFFFF);//|0x20000000;	// set priority 1
	NVIC_ST_CTRL_R = 0x00000007;				// enable clock and interrupt 
}

// *******************SysTick_Handler***********************
// Timer handler to check for switch pressing, flashing LED,
// and detecting pot movement
// Input: None
// Output: None
// Note: if player is shooting the normal missle (PE0), normal LED (PB4) toggle
// 			 if player is shooting the special missle (PE1), special LED (PB5)toggle
//		   Slide pot pin 1 connected to ground,Slide pot pin 2 connected to PE2/AIN1 and
// 			 Slide pot pin 3 connected to +3.3V 
void SysTick_Handler(void){
	ADCdata = ADC0_In();
	if (Fire == 0x01){				
		missile_image = Missile0;
		if ((Previous_Fire == 0)|| (Previous_Fire == 2)){
			enemy_hit_flag = 1;
			R_number = Rand(80,5);
			flag_sound = 1;										// set flag to 1 to indicate normal firing
			Previous_Fire = 1;
		}
		GPIO_PORTB_DATA_R ^= 0x10;				// toggle PB4
	}
	else if (Fire == 0x02){
		missile_image = Laser0;
		if ((Previous_Fire == 0) || (Previous_Fire == 1)){
			enemy_hit_flag = 1;
			srand(NVIC_ST_CURRENT_R);
			R_number = Rand(80,5);
			flag_sound = 2;										// set flag to 2 to indicate special firing
			Previous_Fire = 2;
		}
		GPIO_PORTB_DATA_R ^= 0x20;				// toggle PB5
	}
	else{
		GPIO_PORTB_DATA_R = 0x00;				// turn off both LEDs
		flag_sound = 0;									// reset flag 
		Previous_Fire = 0;
	}
}

// **************PORTS_Init*********************
// Subroutine to initialize port E for the switches (used to fire weapons) and port B for the LEDs (used to fire indication)
// PE0 and PE1 are inputs, and PB4 and PB5 are outputs
// Inputs: None
// Output: None
// Notes: PE0 is used to fire normal weapon which toggles PB4 while PE1 is for firing the special weapon that toggles PB5
void PORTS_Init(void){
	volatile unsigned long delay;
	
	SYSCTL_RCGC2_R |= 0x00000012;			// 1) enable port E and B clock
	delay = SYSCTL_RCGC2_R; 					// delay
	
	// port E initialization
	GPIO_PORTE_AMSEL_R &= ~0x03;			// 2) disable analog function 
	GPIO_PORTE_PCTL_R	&= ~0x000000FF;	// 3) PCTL GPIO on PE0 and PE1 (to select regular digital function)
	GPIO_PORTE_DIR_R &= ~0x03; 				// 4) set PE0 and PE1 as inputs
	GPIO_PORTE_AFSEL_R &= ~0x03; 			// 5) clear bits in alternate function
	GPIO_PORTE_DEN_R |= 0x03;					// 6) enable digital pins PE0 and PE1
	
	// port B initialization
	GPIO_PORTB_AMSEL_R = 0x00;				// 2) disable analog function
	GPIO_PORTB_PCTL_R	= 0x00000000;		// 3) PCTL GPIO on PB4 and PB5 (to select reular digital function)
	GPIO_PORTB_DIR_R |= 0x30; 				// 4) set PB4 and PB5 as outputs
	GPIO_PORTB_AFSEL_R = 0x00; 				// 5) clear bits in alternate function
	GPIO_PORTB_DEN_R |= 0x30;					// 6) enable digital pins PB4 and PB5
}

// **************DAC_Init*********************
// Subroutine to initialize a 4-bit DAC to make sounds
// Inputs: None
// Output: None
// Notes: The DAC uses the ports PB0-3
// 				This subroutine runs at 11kHz interrupt
void DAC_Init(void){
	unsigned long volatile delay;
	SYSCTL_RCGC2_R |= SYSCTL_RCGC2_GPIOB;	// activiate clock on port B
	delay = SYSCTL_RCGC2_R;
	GPIO_PORTB_AMSEL_R = ~0x0F;						// disable analog function on PB3-0
	GPIO_PORTB_PCTL_R = ~0x0000FFFF;			// enable digital function on PB3-0
	GPIO_PORTB_DR8R_R |= 0x0F;						// enable 8mA drive on PB3-0
	GPIO_PORTB_DIR_R |= 0x0F;						  // make PB3-0 outputs
	GPIO_PORTB_AFSEL_R &= ~0x0F;					// diable alternate function (enable regular function) on PB3-0
	GPIO_PORTB_DEN_R |= 0x0F;							// enable digital port
}

// **************DAC_Out*********************
// output to DAC
// Input: 4-bit data, 0 to 15 
// Output: none
void DAC_Out(unsigned long data){
	GPIO_PORTB_DATA_R = data;							// output data to DAC
}

// **************Timer2_Init*********************
// Timer to play sounds
// Input: period (the divider for the frequency)
// Output: None
// Note: this timer has priority of 0 and runs at 11kHz 
void Timer2_Init(unsigned long period){ 
  unsigned long volatile delay;
  SYSCTL_RCGCTIMER_R |= 0x04;   // 0) activate timer2
  delay = SYSCTL_RCGCTIMER_R;
  TIMER2_CTL_R = 0x00000000;    // 1) disable timer2A during setup
  TIMER2_CFG_R = 0x00000000;    // 2) configure for 32-bit mode
  TIMER2_TAMR_R = 0x00000002;   // 3) configure for periodic mode, default down-count settings
  TIMER2_TAILR_R = period-1;    // 4) reload value
  TIMER2_TAPR_R = 0;            // 5) bus clock resolution
  TIMER2_ICR_R = 0x00000001;    // 6) clear timer2A timeout flag
  TIMER2_IMR_R = 0x00000001;    // 7) arm timeout interrupt
  NVIC_PRI5_R = (NVIC_PRI5_R&0x00FFFFFF); // 8) priority 0
// interrupts enabled in the main program after all devices initialized
// vector number 39, interrupt number 23
  NVIC_EN0_R = 1<<23;           // 9) enable IRQ 23 in NVIC
  TIMER2_CTL_R = 0x00000001;    // 10) enable timer2A
}

// **************Timer2_Handler*********************
// Handler to handle timer events 
// Input: None
// Output: None
// Note: This timre reads a flag set by SysTick and 
// 				sound the sound for firing accordingly 
void Timer2A_Handler(void){
  TIMER2_ICR_R = 0x00000001;   				// acknowledge timer2A timeout
	Fire = GPIO_PORTE_DATA_R & 0x03;		// reading the switches
	if (flag_sound == 1){								// if switch (PE0) is pressed
		if (Index_N<1802){								// 1802 is the size of the highpitch array
			Index_N = (Index_N+1)&0x7FF;		// index to cycle over sin wave (0x7FF would cycle over the 1802 table)
			DAC_Out(highpitch[Index_N]);		// output the sine wave value to DAC
		}
	}
	else if (flag_sound == 2){
		if (Index_S < 4080){							// 4080 is the size of the shoot array
			Index_S = (Index_S+1)&0xFFF;		// index to cycle over sin wave (0xFFF would cycle over the 4080 table)
			DAC_Out(shoot[Index_S]);				// output the sine wave value to DAC
		}
	}
	else{
		GPIO_PORTB_DATA_R = 0x00;		
		Index_N = Index_S = 0;						// reset indeces
	}
}
// **************Delayms*********************
// Subroutine to add a delay
// Inputs: number of 1ms delays
// Output: None
// Notes: assumes 80 MHz clock
void Delayms(unsigned long ms){
	unsigned long delay;
	while (ms>0){
		delay = 16000;
 		while (delay > 0){
			delay -= 1;
		}
		ms -= 1;
	}
}

// ***********************Rand**************************
// Random number generator between specified two numbers
// Inputs: high: maximum number
//					low: minimum number
// Output: unisgned char that is the generated random number
// Notes: ...
unsigned char Rand(unsigned char low, unsigned char high){
	unsigned char number;
	number = ((rand()>>24)%high)+1;			// random number between low-hight 
	if (number <= low){
		number = low;
	}
	return number; 
}

